import React, { useState, useEffect } from "react";
import axios from "axios";
import { Table } from "@web3uikit/core";
import { Reload } from '@web3uikit/icons'

function Tokens({wallet, setTokens}) {

  const [tokens, setTokenBalances] = useState([]);

  useEffect(() => {
    const getTokenBalances = async () => {
      try {
        const response = await axios.get("http://localhost:8080/tokenBalances", {
          params: {
            address: wallet
          }
        });
        console.log({responses: response.data});
        if(response.data){
          setTokenBalances(response.data);
        }
      } catch (e) {
        console.log(e.message);
      }
    };

    getTokenBalances();
  }, [wallet]);

  return (
    <>
      <div className="tabHeading">ERC20 Coins <Reload onClick={() => setTokenBalances(prevBalances => {
        const getTokenBalances = async () => {
          try {
            const response = await axios.get("http://localhost:8080/tokenBalances", {
              params: {
                address: wallet
              }
            });
            console.log({responses: response.data});
            if(response.data){
              return response.data;
            }
          } catch (e) {
            console.log(e.message);
          }
        };

        getTokenBalances();
      })} />

      {tokens.length > 0 && (
        <Table
          pageSize={6}
          noPagination={true}
          style={{width:"927px"}}
          columnsConfig="300px 300px 300px "
          data={tokens.map((item) => [item.symbol, (Number(item.balance) / Number(`1E${item.decimals}`)).toFixed(3), `$${((Number(item.balance) / Number(`1E${item.decimals}`)) *Number(item.usd)).toFixed(2)}`])}

          header={[
            <span>Currency</span>,
            <span>Balance</span>,
            <span>Value</span>,

          ]}
        />
      )}
      </div>
    </>
  );
}

export default Tokens;
