import React from "react";
import axios from "axios";
import { Table } from "@web3uikit/core";
import { Reload } from "@web3uikit/icons";

function Tokens({ wallet, tokens, setTokens }) {



  async function getTokenBalances() {
    const response = await axios.get("http://localhost:8080/tokenBalances", {
      params: {
        address: wallet,


      },
    });

    if (response.data) {
      const tokenArray = Object.values(response.data);
      tokenProcessing(tokenArray);
    }

  }


  function tokenProcessing(t) {
    const processedTokens = Object.values(t).map((tokenArray) => {
      if (Array.isArray(tokenArray) && tokenArray.length > 0) {
        return tokenArray.map((token) => {
          const bal = (Number(token.balance) / Number(`1E${token.decimals}`)).toFixed(3);
          const val = ((Number(token.balance) / Number(`1E${token.decimals}`)) * Number(token.usd)).toFixed(2);

          return {
            symbol: token.symbol,
            bal,
            val: `$${val}`,
            chain: token.chain,
          };
        });
      }
      return [];
    });

    setTokens(processedTokens.flat());
  }

  return (
    <>
      <div className="tabHeading">ERC20 Tokens <Reload onClick={getTokenBalances} />

      {tokens.length > 0 && (
        <Table
          pageSize={6}
          noPagination={true}
          style={{ width: "1200px" }}
          columnsConfig="300px 300px 300px 300px"
          data={tokens.map((e) => [e.symbol, e.bal, e.val, e.chain])}
          header={[
            <span>Currency</span>,
            <span>Balance</span>,
            <span>Value</span>,
            <span>Chain</span>,
          ]}
        />
      )}
      </div>
    </>
  );
}

export default Tokens;
