import React, { useState } from "react";
import axios from "axios";
import { Table } from "@web3uikit/core";
import { Reload } from '@web3uikit/icons'

function NativeTokens({wallet}) {
  const [nativeBalances, setNativeBalances] = useState([]);


  const getNativeBalances = async () => {
    try {
      const response = await axios.get("http://localhost:8080/nativeBalances", {
        params: {
          address: wallet
        }
      });
      setNativeBalances(response.data?.nativeBalances ?? []);
    } catch (e) {
      console.log(e.message);
    }
  }

  return (
    <>
      <div className="tabHeading">Native Balance <Reload onClick={() => getNativeBalances()}/>

        <Table
          pageSize={8}
          noPagination={true}
          style={{width:"900px"}}
          columnsConfig="300px 300px 250px"
          data={nativeBalances}
          header={[
            <span>Chain</span>,
            <span>Symbol</span>,
            <span>Balance</span>,
            <span>Value</span>,
          ]}
        />
      </div>
    </>
  );
}

export default NativeTokens;
