import React from "react";
import axios from "axios";
import { useState, useEffect } from "react";
import { Reload } from "@web3uikit/icons";
import { Input } from "@web3uikit/core"
import _ from 'lodash';



function Nfts({ chain, wallet, filteredNfts, setFilteredNfts, nfts, setNfts }) {
  const [nameFilter, setNameFilter] = useState("");
  const [idFilter, setIdFilter] = useState("");

  async function getUserNfts() {
    const response = await axios.get("http://localhost:8080/nftBalance", {
      params: {
        address: wallet,
        chain: chain,
      },
    });

    if (response.data.result) {
      nftProcessing(response.data.result);
    }
  }
  async function nftProcessing(t) {
    try {
        const processedNfts = await Promise.all(t.map(async nft => {
            let meta = JSON.parse(nft.metadata);
            if (meta && meta.image) {
                if (meta.image.startsWith("ipfs://")) {
                    nft.image = "https://ipfs.moralis.io:2053/ipfs/" + meta.image.replace("ipfs://", "");
                }
                else if (meta.image.startsWith("Qm")) {
                    nft.image = "https://ipfs.infura.io/ipfs/" + meta.image;
                }
                else if (meta.image.startsWith("https://")) {
                    nft.image = meta.image;
                } else {
                    throw new Error("Invalid image url")
                }
                try {
                    const response = await axios.get(nft.image);
                    if (response.status === 200) {
                        return nft;
                    }
                } catch (e) {
                    console.log(e);
                }
            }
        }));
        setNfts(processedNfts);
      setFilteredNfts(processedNfts);
    } catch(e){
      console.log(e);
    }
  }





  useEffect(() => {
    if (idFilter === "" && nameFilter === "") {
      return setFilteredNfts(nfts);
    }

    let filNfts = [];

    for (let i = 0; i < nfts.length; i++) {
      if (
        nfts[i].name.toLowerCase().includes(nameFilter) &&
        idFilter.length === 0
      ) {
        filNfts.push(nfts[i]);
      } else if (
        nfts[i].token_id.includes(idFilter) &&
        nameFilter.length === 0
      ) {
        filNfts.push(nfts[i]);
      } else if (
        nfts[i].token_id.includes(idFilter) &&
        nfts[i].name.toLowerCase().includes(nameFilter)
      ) {
        filNfts.push(nfts[i]);
      }
    }

    setFilteredNfts(filNfts);
  }, [nameFilter, idFilter]);

  return (
    <>
      <div className="tabHeading">
        NFT Portfolio <Reload onClick={getUserNfts} />
      </div>
      <div className= "filters">
      <Input
          id="NameF"
          label="Name Filter"
          labelBgColor="rgb(33, 33, 38)"
          value={nameFilter}
          style={{}}
          onChange={(e) => setNameFilter(e.target.value)}
        />
        <Input
          id="IdF"
          label="Id Filter"
          labelBgColor="rgb(33, 33, 38)"
          value={idFilter}
          style={{}}
          onChange={(e) => setIdFilter(e.target.value)}
        />
        </div>
        <div className="nftList">
        {filteredNfts.length > 0 &&

          filteredNfts.map((e) => {
            return (
              <>
                <div className="nftInfo">
                {e.image && <img src={e.image} width={200} />}

                <div>Name: {e.name}, </div>
                <div>(ID: {e.token_id.slice(0,5)})</div>
                </div>
              </>
            );
          })
          }
          </div>

    </>
  )};


export default Nfts;
