import React, { useState } from "react";
import axios from "axios";
import { Table } from "@web3uikit/core";
import { Reload } from '@web3uikit/icons'

function NativeTokens({wallet, nativeBalances, setNativeBalances}) {


      async function getNativeBalances(){
      const response = await axios.get("http://localhost:8080/nativeBalances", {
        params: {
          address: wallet,
        },
      });

      console.log({ responses: response.data.nativeBalances });
if (response.data) {
NativeTokenProcessing(response?.data?.nativeBalances ?? []);
}
}

      function NativeTokenProcessing(n) {
        if (Array.isArray(n)) {
        for (let i = 0; i < n.length; i++) {
        n[i].val = (Number(n[i].balance) * Number(n[i].price)).toFixed(2);
        }

        setNativeBalances(n);
        }
        }

  return (
    <>
      <div className="tabHeading">Native Coins <Reload onClick={() => getNativeBalances()}/>

      {nativeBalances.length > 0 && (
        <Table
          pageSize={6}
          noPagination={true}
          style={{width:"927px"}}
          columnsConfig="300px 300px 300px "
          data={nativeBalances.map((item)=>Object.values(item))}

          header={[
            <span>NativeToken</span>,
            <span>Price</span>,
            <span>Value</span>,

          ]}
        />
      )}
      </div>
    </>
  );
}

export default NativeTokens;
